insert into board values(board_seq.nextval,'admin',sysdate,'객실료를 다 지불했는데 현지 추가금을 왜 내야 하나요?','고객님께서 예약 시 지불하시는 요금에는 객실에 대한 세금 및 서비스 요금이 포함되어 있어요. 단, 일부 도시 및 호텔에서는 지불하신 호텔 요금 외에 숙박시설을 이용하는 모든 여행 고객에게 현지 세금을 포함한 추가적인 요금을 요구할 수 있어요. 이러한 특별 추가 요금은 호텔에 직접 지불하는 방식으로, 금액 및 포함 내용이 현지 도시 및 호텔의 사정으로 변경될 수 있어요. 해당 호텔에서 추가금이 발생하는지는 호텔 정보의 세부 사항에서 확인하실 수 있어요. 보증금은 투숙 기간 객실 파손이 없거나 유료 서비스를 이용하지 않으시는 경우 체크아웃하실 때 100% 환불되는 금액으로 체크인 시 호텔에 직접 지불하셔야 하는 금액이에요.',21,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'장기 투숙 예약이 가능한가요?','예약은 한 번에 최장 28박까지 예약하실 수 있어요. 그 이상의 투숙을 원하실 경우에는 예약을 나누어서 진행해주세요.',21,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'다른 사람이 대신 투숙할 수 있나요?','예약 시 남겨주신 투숙객 명과 체크인하시는 분의 이름이 일치하지 않을 경우, 호텔에서는 체크인을 거부할 수 있는 권한을 갖고 있어요. 이 경우 예약 시 남겨주신 대표 투숙객 명을 실제 투숙객 명으로 변경해야 해요. 호텔에 변경 가능 여부를 문의한 뒤 체크인 하루 전이나 당일에는 요청 자체가 어려울 수 있으므로 예약 시 반드시 투숙객 명을 확인해주세요.',21,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'호텔 예약 시 언제 요금이 부과되나요?','숙소 예약 시 숙박료의 전액을 지불하게 돼요.',21,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'객실은 몇 인까지 투숙할 수 있나요?','객실 투숙 인원은 각 호텔의 규정에 따라 다르며, 각각의 객실 타입에 따라 최대 투숙 가능 인원이 표기되어 있어요. 객실 검색 시 검색이 되는 객실은 입력하신 인원으로 투숙이 가능한 객실이에요. 또한, 인원을 모두 입력하셨더라도 호텔 규정에 따라 2인 이상 투숙 시 추가금이 발생할 수 있어요.',21,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'체크인을 위해 호텔에 가져가야 할 준비물은 무엇인가요?','예약 시 발급 된 예약 번호와 신분증 (해외의 경우에는 여권)만 있으면 체크인이 가능해요. 빠른 예약 확인을 위해 예약확인서(바우처)를 출력하여 가져가시는 편이 더욱 좋아요. 예약 확인서는 마이페이지 내 예약 관리나 예약 확인 이메일에서 인쇄하실 수 있어요. 숙박 요금은 예약 시 트립비토즈를 통해 모두 지불하시게 되지만, 호텔 숙박 중 추가로 사용하는 주차 또는 부대시설 이용 요금의 보증금 용도로 호텔에서 신용카드나 직불카드의 정보를 요청할 수 있어요. 이렇게 숙박 중 추가된 비용은 호텔 규정에 따라 해당 호텔에서 직접 청구하고 결제해요.',22,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'얼리 체크인 또는 레이트 체크아웃이 가능한가요?','얼리 체크인 및 레이트 체크 아웃은 당일의 잔여 객실 현황 및 호텔 규정에 따라 가능 여부가 달라질 수 있어요. 단, 사전에는 확인할 수 없으므로 당일에 호텔과 직접 확인하셔야 하며, 호텔 규정에 따라 이용이 가능할 경우에는 추가 비용을 지불해야 할 수도 있어요.',22,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'체크인 전/후에 호텔에 가방을 보관할 수 있나요?','일부 호텔의 경우 체크인 전/후에 가방을 보관해주는 서비스를 제공하며 호텔에 따라 유료로 운영되어요. 가방 보관 서비스의 이용 가능 여부 또는 보관에 관한 규정은 해당 호텔로 문의해 주세요.',22,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'호텔의 부대시설 이용은 무료인가요?','호텔의 운영 정책에 따라 해당 부대시설의 이용 요금을 추가로 지불해야 할 수도 있어요. 이에 대한 자세한 사항은 해당 호텔로 문의해 주세요. (부대시설 운영 방침이 유동적으로 변동될 수 있어요.)',22,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'검색은 어떻게 하나요?','숙소의 정보를 검색하고 싶은 경우에는 지역명 혹은 숙소 명을 검색하여 확인할 수 있으며, 다양한 검색 필터를 활용하여 원하는 날짜에 원하는 형태의 숙소를 검색이 가능해요!',23,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'비밀번호를 변경하고 싶어요','비밀번호 변경은 MY 메뉴 내 정보 페이지에서 변경할 수 있어요.',24,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'계정이 정지당했어요','규정을 위반한 이용자의 계정을 정지시킬 수 있어요. 만약, 규정을 위반하지 않았음에도 계정이 정지되었다면 고객센터 로 문의해주세요.',24,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'탈퇴한 아이디로 재가입 할 수 있나요?','재가입을 원할 경우 회원가입을 통해 재가입이 가능합니다',24,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'회원 정보 수정은 어떻게 하나요?','회원 정보 수정은 MY 메뉴 내 내정보 수정 페이지의 개인 정보 영역에서 수정이 가능해요.',24,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'아이디, 비밀번호를 잃어버렸어요.','아이디,비밀번호 찾기 페이지에서 가입했던 정보를 기입하여 아이디찾기 / 비밀번호 변경이 가능합니다.',24,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'북마크 한 콘텐츠는 어디서 볼 수 있나요?','내가 북마크한 콘텐츠들은 MY 메뉴 내 북마크 탭에서 확인할 수 있어요. 트립비토즈에서 북마크한 콘텐츠들은 모두 지역을 기반으로 그룹화되어 여행을 계획 중이라면 더 알찬 정보가 될 거에요.',25,0,0);
insert into board values(board_seq.nextval,'admin',sysdate,'북마크가 무엇인가요?','맘에 들어서 다시 보고 싶은 영상, 트립톡의 좋은 정보 그리고 관심 있는 호텔의 북마크가 가능해요. 북마크를 눌러서 언제든 확인해보세요.',25,0,0);
